import { ENTITY_FILE_NAME } from '../Constants'
import { EntityType, Pointer, Timestamp, EntityMetadata, Entity, EntityContentItemReference, ContentFile, EntityId } from "../types"
import { Hashing } from "./Hashing"

/**
 * Take all the entity's data, build the entity file with it, and calculate its id
 */
export async function buildEntityAndFile(type: EntityType, pointers: Pointer[], timestamp: Timestamp,
    content?: EntityContentItemReference[], metadata?: EntityMetadata): Promise<{entity: Entity, entityFile: ContentFile}> {

    // Make sure that there is at least one pointer
    if (pointers.length === 0) {
        throw new Error(`All entities must have at least one pointer.`)
    }

    const entity = {
        type,
        pointers,
        timestamp,
        content,
        metadata
    }

    const entityFile: ContentFile = { name: ENTITY_FILE_NAME, content: Buffer.from(JSON.stringify(entity)) }
    const entityId: EntityId = await Hashing.calculateHash(entityFile)
    const entityWithId: Entity = {
        id: entityId,
        ...entity
    }

    return { entity: entityWithId, entityFile}
}

import CID from 'cids'
import multihashing from 'multihashing-async';
import { ContentFileHash, ContentFile } from '../types';

export class Hashing {

    /** Given a set of files, return a map with their hash */
    static async calculateHashes(files: ContentFile[]): Promise<{ hash: ContentFileHash, file: ContentFile }[]> {
        const entries = Array.from(files)
            .map<Promise<{hash: ContentFileHash, file: ContentFile}>>(async file => ({ hash: await this.calculateHash(file), file }))
        return Promise.all(entries);
    }

    /** Return the given file's hash */
    static async calculateHash(file: ContentFile): Promise<ContentFileHash> {
        return this.calculateBufferHash(file.content)
    }

    /** Return the given buffer's hash */
    static async calculateBufferHash(buffer: Buffer): Promise<ContentFileHash> {
        const hash = await multihashing(buffer, "sha2-256")
        return new CID(0, 'dag-pb', hash).toBaseEncodedString()
    }
}

import ms from 'ms'
import AbortController from 'abort-controller'
import { clearTimeout, setTimeout } from 'timers'
import { mergeRequestOptions, retry } from './Helper'
import blobToBuffer from 'blob-to-buffer'
import crossFetch from 'cross-fetch'

import {
  CompleteRequestOptions,
  FETCH_BUFFER_DEFAULTS,
  FETCH_JSON_DEFAULTS,
  POST_DEFAULTS,
  RequestOptions
} from './FetcherConfiguration'

export class Fetcher {
  private readonly customDefaults: Omit<RequestOptions, 'body'>

  constructor(customDefaults?: Omit<RequestOptions, 'body'>) {
    this.customDefaults = customDefaults ?? {}
  }

  fetchJson(url: string, options?: RequestOptions): Promise<any> {
    return fetchJson(url, mergeRequestOptions(this.customDefaults, options))
  }

  fetchBuffer(url: string, options?: RequestOptions): Promise<Buffer> {
    return fetchBuffer(url, mergeRequestOptions(this.customDefaults, options))
  }

  /**
   * Fetches the url and pipes the response obtained from the upstream to the `writeTo` Stream and
   *  returns the headers from the upstream request.
   * @param url to request
   * @param writeTo the stream to pipe the response to
   * @param options config for the request
   */
  fetchPipe(url: string, writeTo: ReadableStream<Uint8Array>, options?: RequestOptions): Promise<Headers> {
    return fetchPipe(url, writeTo, mergeRequestOptions(this.customDefaults, options))
  }

  postForm(url: string, options?: RequestOptions): Promise<any> {
    return postForm(url, mergeRequestOptions(this.customDefaults, options))
  }

  queryGraph<T = any>(
    url: string,
    query: string,
    variables: Record<string, any>,
    options?: RequestOptions
  ): Promise<T> {
    return queryGraph(url, query, variables, mergeRequestOptions(this.customDefaults, options))
  }
}

export async function fetchJson(url: string, options?: RequestOptions): Promise<any> {
  return fetchInternal(url, response => response.json(), mergeRequestOptions(FETCH_JSON_DEFAULTS, options))
}

export async function fetchBuffer(url: string, options?: RequestOptions): Promise<Buffer> {
  return fetchInternal(url, response => extractBuffer(response), mergeRequestOptions(FETCH_BUFFER_DEFAULTS, options))
}

/**
 * Fetches the url and pipes the response obtained from the upstream to the `writeTo` Stream and
 *  returns the headers from the upstream request.
 * @param url to request
 * @param writeTo the stream to pipe the response to
 * @param options config for the request
 */
export async function fetchPipe(
  url: string,
  writeTo: ReadableStream<Uint8Array>,
  options?: RequestOptions
): Promise<Headers> {
  return fetchInternal(
    url,
    response => copyResponse(response, writeTo),
    mergeRequestOptions(FETCH_BUFFER_DEFAULTS, options)
  )
}

async function copyResponse(response: Response, writeTo: ReadableStream<Uint8Array>): Promise<Headers> {
  // The method pipeTo() is not working, so we need to use pipe() which is the one implemented
  // eslint-disable-next-line @typescript-eslint/ban-ts-comment
  // @ts-ignore
  response.body.pipe(writeTo)
  return response.headers
}

export async function postForm(url: string, options?: RequestOptions): Promise<any> {
  return fetchInternal(url, response => response.json(), mergeRequestOptions(POST_DEFAULTS, options))
}

export async function queryGraph<T = any>(
  url: string,
  query: string,
  variables: Record<string, any>,
  options?: RequestOptions
): Promise<T> {
  const response = await postForm(
    url,
    Object.assign(
      { body: JSON.stringify({ query, variables }), headers: { 'Content-Type': 'application/json' } },
      options
    )
  )
  if (response.errors) {
    throw new Error(`Error querying graph. Reasons: ${JSON.stringify(response.errors)}`)
  }
  return response.data
}

async function fetchInternal<T>(
  url: string,
  responseConsumer: (response: Response) => Promise<T>,
  options: CompleteRequestOptions
): Promise<T> {
  return retry(
    async () => {
      const controller = new AbortController()
      const timeout = setTimeout(() => {
        controller.abort()
      }, ms(options.timeout))

      try {
        const response: Response = await crossFetch(url, {
          signal: controller.signal,
          body: options.body,
          method: options.method,
          headers: options.headers
        })
        if (response.ok) {
          return await responseConsumer(response)
        } else {
          const responseText = await response.text()
          throw new Error(`Failed to fetch ${url}. Got status ${response.status}. Response was '${responseText}'`)
        }
      } finally {
        clearTimeout(timeout)
      }
    },
    options.attempts,
    options.waitTime
  )
}

async function extractBuffer(response: Response): Promise<Buffer> {
  if ('buffer' in response) {
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    return response.buffer()
  }
  const blob = await response.blob()
  return asyncBlobToBuffer(blob)
}

function asyncBlobToBuffer(blob: Blob): Promise<Buffer> {
  return new Promise((resolve, reject) => {
    blobToBuffer(blob, (err: Error, buffer: Buffer) => {
      if (err) reject(err)
      resolve(buffer)
    })
  })
}

import ms from 'ms'
import { CompleteRequestOptions, RequestOptions } from './FetcherConfiguration'

export function delay(time: string): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms(time)))
}

export async function retry<T>(
  execution: () => Promise<T>,
  attempts: number,
  waitTime: string = '1s',
  failedAttemptCallback?: (attemptsLeft: number) => void,
  allFailedCallback?: () => void
): Promise<T> {
  while (attempts > 0) {
    try {
      return await execution()
    } catch (error) {
      attempts--
      if (attempts > 0) {
        if (failedAttemptCallback) {
          failedAttemptCallback(attempts)
        }
        await delay(waitTime)
      } else {
        if (allFailedCallback) {
          allFailedCallback()
        }
        return Promise.reject(error)
      }
    }
  }
  return Promise.reject(new Error('Should never reach here'))
}

/** Add defaults to missing properties in the partial object */
export function applyDefaults<T, K = T | Partial<T>>(defaults: K, partial?: Partial<T>): K {
  return { ...defaults, ...partial }
}

/** Add some defaults to missing properties in the partial object. This means that the object is not yet complete */
export function applySomeDefaults<T>(defaults: Partial<T>, partial?: Partial<T>): Partial<T> {
  return { ...defaults, ...partial }
}

/**  As headers field is Record<string, string> type, then when merging request Options
     it's needed to merge the array instead of just applying the defaults.           */
export function mergeRequestOptions<T = CompleteRequestOptions | RequestOptions>(
  target: T,
  source?: RequestOptions
): T {
  const combinedHeaders: Record<string, string> = {
    ...(target as RequestOptions)?.headers,
    ...source?.headers
  }
  const combinedOptions: T = applyDefaults(target, source)
  return { ...combinedOptions, headers: combinedHeaders }
}

export type RequestOptions = Partial<CompleteRequestOptions>

export type CompleteRequestOptions = {
  method: string
  attempts: number // Number of attempts to perform the request
  timeout: string // Time to abort the request. Time format accepted by ms: Examples: '0.5s', '2m', '3h', '100' (assumed to be milliseconds)
  waitTime: string // Time to wait between attempts. Time format accepted by ms: Examples: '0.5s', '2m', '3h', '100' (assumed to be milliseconds)
  body?: FormData | string
  headers?: Record<string, string>
}

export const FETCHER_DEFAULTS = {
  method: 'GET',
  attempts: 1,
  waitTime: '1s',
  timeout: '5m'
}

export const FETCH_JSON_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  timeout: '30s',
  waitTime: '0.5s'
}
export const FETCH_BUFFER_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  timeout: '1m'
}
export const POST_DEFAULTS = {
  ...FETCHER_DEFAULTS,
  method: 'POST'
}

import { CatalystData, DAOContract } from '../contracts/CatalystContract'
